import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./Sidebar.css";

export default function Sidebar() {

  const [erpOpen, setErpOpen] = useState(false);
  const [userConfigOpen, setUserConfigOpen] = useState(false);

  return (
    <div className="sidebar">

      {/* LOGO */}
      <div className="sidebar-logo">
        Produlink
      </div>

      <ul className="sidebar-menu">

        {/* DASHBOARD */}
        <li>
          <NavLink to="/dashboard">Dashboard</NavLink>
        </li>

        {/* ERP SETTING */}
        <li className="menu-item" onClick={() => setErpOpen(!erpOpen)}>
          <div className="menu-title">
            ⚙ ERP Setting
            <span>{erpOpen ? "▲" : "▼"}</span>
          </div>
        </li>

        {/* ERP SUB MENU */}
        {erpOpen && (
          <ul className="submenu">

            {/* USER CONFIGURATION */}
            <li
              className="submenu-title"
              onClick={() => setUserConfigOpen(!userConfigOpen)}
            >
              User Configuration ▶
            </li>

            {/* USER CONFIG CHILD MENU */}
            {userConfigOpen && (
              <ul className="child-submenu">

                <li><NavLink to="/user-management">User Management</NavLink></li>
                <li><NavLink to="/user-permission">User Permission</NavLink></li>
                <li><NavLink to="/dashboard-permission">Dashboard Permission</NavLink></li>
                <li><NavLink to="/backdate-setting">Back Dated Entry Setting</NavLink></li>
                <li><NavLink to="/user-wise-series">User Wise Series</NavLink></li>
                <li><NavLink to="/prod-operation">Userwise Prod. Operation</NavLink></li>
                <li><NavLink to="/auth-setting">Userwise Auth. Setting</NavLink></li>
                <li><NavLink to="/user-plant">User Plant</NavLink></li>
                <li><NavLink to="/plant-wise-series">Plant Wise Series</NavLink></li>

              </ul>
            )}

            {/* OTHER ERP OPTIONS (Optional) */}
            <li><NavLink to="/erp-config">ERP Configuration</NavLink></li>
            <li><NavLink to="/change-password">Change Password</NavLink></li>

          </ul>
        )}

        {/* OTHER MAIN MENU */}
        <li><NavLink to="/masters">Masters</NavLink></li>
        <li><NavLink to="/purchase">Purchase</NavLink></li>

      </ul>

    </div>
  );
}
